content.query:
let TotalSignIns = SigninLogs
| where TimeGenerated {TimeRange}
| count
| project TotalSignIns = Count;
let FailedSignIns = SigninLogs
| where TimeGenerated {TimeRange}
| where ResultType != 0
| count
| project FailedSignIns = Count;
let RiskySignIns = SigninLogs
| where TimeGenerated {TimeRange}
| where RiskLevelDuringSignIn in ("high", "medium")
| count
| project RiskySignIns = Count;
let PrivilegedAccess = AuditLogs
| where TimeGenerated {TimeRange}
| where Category == "RoleManagement" or OperationName contains "role"
| count
| project PrivilegedAccess = Count;
TotalSignIns
| extend FailedSignIns = toscalar(FailedSignIns)
| extend RiskySignIns = toscalar(RiskySignIns)
| extend PrivilegedAccess = toscalar(PrivilegedAccess)
| extend ComplianceScore = case(
    TotalSignIns == 0, 100.0,
    round((1.0 - (todouble(FailedSignIns + RiskySignIns) / todouble(TotalSignIns))) * 100, 2)
)
| project TotalSignIns, FailedSignIns, RiskySignIns, PrivilegedAccess, ComplianceScore
| evaluate narrow()
| project Indicator = Column, Value
